# Replace these variable sources with actual values:
$LockScreenSource = 'C:\pathToImages\lockImage.jpg'
$BackgroundSource = 'C:\pathToImages\backgroundImage.jpg'

Function Update-LockAndBackgroundScreen{
    Param(
            [Parameter(Mandatory=$false,Position=0)]
            [ValidateNotNullOrEmpty()]
            [string]$LockScreenSource,
            [Parameter(Mandatory=$false,Position=1)]
            [ValidateNotNullOrEmpty()]
            [string]$BackgroundSource
    )
    Begin {
        $ErrorActionPreference = "Stop"
        $RegKeyPath = "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\PersonalizationCSP"
        $DesktopPath = "DesktopImagePath"
        $DesktopStatus = "DesktopImageStatus"
        $DesktopUrl = "DesktopImageUrl"
        $LockScreenPath = "LockScreenImagePath"
        $LockScreenStatus = "LockScreenImageStatus"
        $LockScreenUrl = "LockScreenImageUrl"
        $StatusValue = "1"
        $DesktopImageValue = "C:\Windows\System32\Desktop.jpg"
        $LockScreenImageValue = "C:\Windows\System32\LockScreen.jpg"
    }
    process{
        if (!$LockScreenSource -and !$BackgroundSource)
        {
            Write-Host "Either LockScreenSource or BackgroundSource must has a value."
        }
        else
        {
            if(!(Test-Path $RegKeyPath)) {
                Write-Host "Creating registry path $($RegKeyPath)."
                New-Item -Path $RegKeyPath -Force | Out-Null
            }
            if ($LockScreenSource) {
                Write-Host "Copy Lock Screen image from $($LockScreenSource) to $($LockScreenImageValue)."
                Copy-Item $LockScreenSource $LockScreenImageValue -Force
                Write-Host "Creating registry entries for Lock Screen"
                New-ItemProperty -Path $RegKeyPath -Name $LockScreenStatus -Value $StatusValue -PropertyType DWORD -Force | Out-Null
                New-ItemProperty -Path $RegKeyPath -Name $LockScreenPath -Value $LockScreenImageValue -PropertyType STRING -Force | Out-Null
                New-ItemProperty -Path $RegKeyPath -Name $LockScreenUrl -Value $LockScreenImageValue -PropertyType STRING -Force | Out-Null
            }
            if ($BackgroundSource) {
                Write-Host "Copy Desktop Background image from $($BackgroundSource) to $($DesktopImageValue)."
                Copy-Item $BackgroundSource $DesktopImageValue -Force
                Write-Host "Creating registry entries for Desktop Background"
                New-ItemProperty -Path $RegKeyPath -Name $DesktopStatus -Value $StatusValue -PropertyType DWORD -Force | Out-Null
                New-ItemProperty -Path $RegKeyPath -Name $DesktopPath -Value $DesktopImageValue -PropertyType STRING -Force | Out-Null
                New-ItemProperty -Path $RegKeyPath -Name $DesktopUrl -Value $DesktopImageValue -PropertyType STRING -Force | Out-Null
            }
            rundll32.exe user32.dll, UpdatePerUserSystemParameters
        }
    }
}


Update-LockAndBackgroundScreen -LockScreenSource $LockScreenSource -BackgroundSource $BackgroundSource

################################################################################
# This script will disable the *matched* username patterns in the usersToMatch
# list variable. For Example:
# Users on the system: administrator, steve, it-staff
# $usersToMatch = @("admin", "it")
# both the administrator and it-staff would be disabled
# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
# Test how this command will run by setting the disable variable to false. When
# set to false the script will identify users to disable and print out a list
# without disabling the accounts
################################################################################

# Settings, set $true or $false
$disable = $true

# Enter user(s) patterns you want to match against (user admin bobsAccount)
$usersToMatch = @("admin", "it")

# Do not modify below this line
################################################################################

$regexPattern=""
$last = [int]($usersToMatch.Length) - 1
foreach ($user in $usersToMatch) {
    Write-Host $user
    if ($user -eq $usersToMatch[$last]){
        $regexpattern += "$($user)[^\s]*"
    }
    else{
        $regexpattern += "$($user)[^\s]*|"
    }
}
Write-Host "Searching System for the following users: $usersToMatch"
Write-Host "Regex Pattern: $regexPattern"

$localUsers = Get-LocalUser
$foundUsers = @()
foreach ($username in $localUsers.name)
{
    if ($username -match $regexPattern)
    {
        # Set Selected Username Variable
        write-host "Matched $username user found"
        $foundUsers += $username
    }
}

Write-Host "The Following users will be disabled: $foundUsers"
$loggedInUsers=query user
foreach ($username in $foundUsers){
    if ($disable){
        if ($loggedInUsers -match $username) {
            Write-Host "Logging $username out of system"
            $session = quser | Where-Object { $_ -match $username }
            ## Parse the session IDs from the output
            $sessionIds = ($session -split ' +')[2]
            Write-Host "Found $sessionIds for $username on system."
            ## Loop through each session ID and pass each to the logoff command
            $sessionIds | ForEach-Object {
                Write-Host "Logging off session id [$($_)]..."
                logoff $_
            }
        }
        Disable-LocalUser -Name $username
    }
}

function Test-ServiceForUserAccount {
    param (
        $StartName
    )

    if (-not $StartName) {
        $false
        return
    }

    $AccountName = $StartName.Trim().ToLower()

    if (-not $AccountName) {
        $false
        return
    }
    if ($AccountName -eq 'localsystem') {
        $false
        return
    }
    if ($AccountName.StartsWith('nt authority\')) {
        $false
        return
    }

    $true
}

function Write-ServiceInfos {
    param (
        [Parameter(ValueFromPipeline)]
        [object[]]
        $ServiceInfos
    )

    BEGIN {}

    PROCESS {
        foreach ($ServiceInfo in $ServiceInfos) {
            Write-Output('')
            Write-Output('    Name: ' + $ServiceInfo.Name)
            Write-Output('    PathName: ' + $ServiceInfo.PathName)
            Write-Output('    StartMode: ' + $ServiceInfo.StartMode)
            Write-Output('    State: ' + $ServiceInfo.State)
            Write-Output('    ProcessId: ' + $ServiceInfo.ProcessId)
            Write-Output('    StartName: ' + $ServiceInfo.StartName)
        }
    }
    END {}
}

function Test-TaskForUserAccount {
    param (
        $Principal
    )

    if (($Principal.LogonType -eq 'Password') -or ($Principal.LogonType -eq 'InteractiveOrPassword')) {

        if (!($Principal.userid) -or ($Principal.userid -eq "SYSTEM") -or ($Principal.userid -eq "LOCAL SERVICE") -or ($Principal.userid -eq "NETWORK SERVICE")) {
            $false
            return
        }

        $true
        return

    }

    $false
}

function Write-TaskInfos {
    param (
        [Parameter(ValueFromPipeline)]
        [object[]]
        $TaskInfos
    )

    BEGIN {}

    PROCESS {
        foreach ($TaskInfo in $TaskInfos) {
            Write-Output('')
            Write-Output('    TaskName: ' + $TaskInfo.TaskName)
            Write-Output('    UserId: ' + $TaskInfo.Principal.UserId)
            Write-Output('    LogonType: ' + $TaskInfo.Principal.LogonType)
            Write-Output('    Execute: ' + [System.IO.Path]::GetFileName([System.Environment]::ExpandEnvironmentVariables($TaskInfo.Actions.Execute).Trim('"')))
            Write-Output('    Arguments: ' + [System.Environment]::ExpandEnvironmentVariables($TaskInfo.Actions.Arguments))
        }
    }
    END {}
}

Write-Output('Services that run in a user account context:')
Get-WmiObject Win32_Service | Where { Test-ServiceForUserAccount($_.startname) } | Write-ServiceInfos

Write-Output('')
Write-Output('Scheduled tasks that run in a user account context:')
Get-ScheduledTask | Where { Test-TaskForUserAccount($_.Principal) } | Write-TaskInfos

